tinymce.PluginManager.add("addimageleft", function(a, b) {
    var state;

    /* Actions to do on button click */
    function Klik() {
        state = !state; /* Switching state */
        a.fire('addimageleft', {state: state});
        htmldata = "";
        
        htmldata+= '&nbsp;';
        htmldata += ' <div class="timeline-point"><i class="fa fa-circle-o"></i></div>';
        htmldata += ' <div class="timeline-custom-col content-col"><div class="timeline-location-block"><p class="location-name">London England<i class="fa fa-map-marker icon-marker"></i></p><p class="description">Lorem ipsum dolor sit amet, consectetur. Nulla rhoncus ultrices purus, volutpat. Lorem ipsum dolor sit amet, consectetur elit dolor sit amet, consectetur nulla rhoncus ultrices purus. Lorem ipsum dolor sit amet, consectetur elit dolor sit amet. Lorem ipsum dolor sit amet, consectetur elit dolor sit amet, consectetur nulla rhoncus ultrices purus.</p></div>';
         htmldata +=  '</div>';
        htmldata += '<div class="timeline-custom-col image-col"> <div class="timeline-image-block"><img src='+b+'/img/imageright.jpg alt=""></div></div>';
                    
        htmldata+= '&nbsp;';

        a.insertContent(htmldata);
    }

    /* Adding the button & command */
    a.addCommand('cmd_addimageleft', Klik);

    a.addButton("addimageleft", {
        text: "Gambar Kiri",
        icon: 1,
        // image: b + '/img/summary.png',
        cmd: 'cmd_addimageleft',
    }), a.addMenuItem("addimageleft", {
        text: "Gambar Kiri",
        icon: 1,
        // image: b + '/img/summary.png',
        context: "format",
        cmd: 'cmd_addimageleft',
    })
});